script:
PACKAGES=("wget")
unset INSTALL_COMMAND

command -v apt-get >/dev/null 2>&1 && {
    INSTALL_COMMAND="apt-get install"
}

command -v yum >/dev/null 2>&1 && {
    INSTALL_COMMAND="yum install"
}

command -v urpmi >/dev/null 2>&1 && {
    INSTALL_COMMAND="urpmi"
}

command -v rpm >/dev/null 2>&1 && {
    INSTALL_COMMAND="rpm -i"
}

command -v dnf >/dev/null 2>&1 && {
    INSTALL_COMMAND="dnf install"
}

command -v apk >/dev/null 2>&1 && {
    INSTALL_COMMAND="apk add"
}

if [ -z ${INSTALL_COMMAND+1} ]; then
  echo "Install command is not recognized"
  exit 1
fi

for resource in ${PACKAGES[@]}; do
    sudo $INSTALL_COMMAND $resource
done
